        {
            //https://github.com/ingig/code-narrator/tree/master/docs/howto
            type: "README",
            template: "overview_readme",
            name: "README",
            path: "tutorial",
            files: [{
                path: "tutorial/*.md"
            }
            ],
            pages: [
