export interface GenerateOptions {
    id? : string;
    args?: any
    template?: string;
    name: string;
    pathToFile: string
    folderPath: string
    sidebarPosition?: number
    sidebarLabel?: string
    saveToPath?: string;
    data?: any;
}

import Document from "../../Document";

/*
This is the Base class for Generator plugins. Generator plugins are used to parse the documentation
and modify it for the build tool you are using to build the documentation.

Default plugin with code-narrator is Docusaurus since it fails to build when a function contains
a Promise<> in return. This plugin clear that Promise from the function name so it can build.
It also creates sidebar information.
 */
export default abstract class BaseGenerator {

    abstract process(document : Document) : Document;
}

import BaseGenerator from "../BaseGenerator";
import Document from '../../../Document'
export default class DocusaurusGenerator extends BaseGenerator {

    public process(document : Document) {
        //docusaurus does not like to have Promise<> in its headers, so let's remove them
        document.documentation = document.documentation.replaceAll(/: Promise<[a-zA-Z]*>/gm, '').trim();

        let sidebar_label = (document.sidebar_label) ? document.sidebar_label : document.name;
        let position = document.sidebar_position ?? 1;
        document.documentation = `---
sidebar_position: ${position}
sidebar_label: ${sidebar_label}
---

` + document.documentation;
        return document;
    }

}

import {ChatCompletionRequestMessage, Configuration, CreateCompletionResponseChoicesInner, OpenAIApi} from 'openai';
import ConfigHelper from "../utils/ConfigHelper";
import { setTimeout } from 'timers/promises';
import {CreateChatCompletionResponseChoicesInner} from "openai/api";
import config from '../../code-narrator.config'

export default class OpenAIRepository {
    openai: OpenAIApi;
    constructor() {
        const configuration = new Configuration({
            apiKey: ConfigHelper.get('OPENAI_API_KEY')
        });
        this.openai = new OpenAIApi(configuration);
    }


    public async query(text : string) : Promise<CreateCompletionResponseChoicesInner[] | undefined> {
        return this.queryQuestions([text]);
    }
    public async queryQuestions(questions : string[], errorCount = 0, model = 'gpt-4'): Promise<CreateChatCompletionResponseChoicesInner[] | undefined> {
        try {
            await setTimeout(1 * 1000);

            let messages : ChatCompletionRequestMessage[] = []
            let messageLength = 0;
            for (let i=0;config.gptSystemCommands && i<config.gptSystemCommands.length;i++) {
                messages.push({role:'system', content:config.gptSystemCommands[i].replace('{DocumentationType}', ConfigHelper.DocumentationType)})
            }
            for (let i=0;i<questions.length;i++){
                messages.push({role:'user', content:questions[i]})
                messageLength += questions[i].length;
            }
            let maxTokens = (model.indexOf('gpt-4') != -1) ? 8000 : 4000;
            const completion = await this.openai.createChatCompletion({
                model: model,
                messages : messages,
                temperature: 0.1,
                max_tokens: maxTokens - messageLength,
                top_p: 1,
                frequency_penalty: 0,
                presence_penalty: 0

            });
            return completion.data.choices;
        } catch (e : any) {
            if (e?.response?.data?.error && e.response.data.error.message.indexOf('You exceeded your current quota') != -1) {
                throw new Error(e.response.data.error.message);
            }

            if (e && this.retryStatuses.includes(e.response?.status) && errorCount < 3) {
                console.log('Sleep 3 sec')
                await setTimeout(3 * 1000);
                return this.queryQuestions(questions, ++errorCount, model);
            }

            console.error('OpenAI error:', e.response.data.error.message)
            console.error('Error doing OpenAI query:', questions);
            console.error(e);
            return;
            //throw e;
        }
    }

    retryStatuses = [429, 500, 503]
}

import * as dotnet from "dotenv";
import fs from "fs";
import path from "path";
import App from "../App";

export default class ConfigHelper {
    static env : any;
    static cn : any;
    static projectFile : any;
    static BuilderPlugins : any[] = [];
    static DocumentationType = 'md';
    static DocumentExtension = '.md';
    static CacheFilePath = '.code-narrator/cache.json';
    public static async load(config : any, project : any) {
        dotnet.config()
        ConfigHelper.env = process.env;
        ConfigHelper.cn = project;
        ConfigHelper.BuilderPlugins = config.builderPlugins;
        ConfigHelper.DocumentExtension = config.document_file_extension ?? '.md';
        ConfigHelper.DocumentationType = config.documentation_type ?? 'md'
        if (ConfigHelper.DocumentExtension.indexOf('.') != 0) ConfigHelper.DocumentExtension = '.' + ConfigHelper.DocumentExtension;

        ConfigHelper.CacheFilePath = config.cache_file ?? '.code-narrator/cache.json';

        let projectFile = ConfigHelper.get('project_file');
        const projectFileRaw = fs.readFileSync(path.join(process.cwd(), projectFile));
        ConfigHelper.projectFile = JSON.parse(projectFileRaw.toString());
        if (ConfigHelper.projectFile['repository']) {
            App.repositoryUrl = ConfigHelper.projectFile['repository']['url']
        }
    }
    public static get(key : string) {
        let value = ConfigHelper.env[key];
        if (value) return value;
        value = ConfigHelper.cn[key] ?? '';
        if (value) return value;
        return ConfigHelper.projectFile[key] ?? '';
    }

}

import path, {resolve} from "path";
import fs from "fs";
import ConfigHelper from "./ConfigHelper";

export default class FileStructure {
    name = '';
    path = '';
    entry : any;

    constructor(dir : string, entry : any) {
        this.name = entry.name;
        let project_path = ConfigHelper.get('project_path');
        this.path = path.relative(project_path, resolve(dir, entry.name));
        this.entry = entry;
    }

    public static isCodeFile(fileName: string) {
        return path.extname(fileName).indexOf('.ts') != -1;
    }


    static getContent(path: string) : string {
        if (!fs.existsSync(path)) return '';

        return fs.readFileSync(path).toString();
    }
}

import fs, {existsSync, readdirSync} from "fs";
import FileStructure from "./FileStructure";
import ConfigHelper from "./ConfigHelper";
import path, {resolve} from "path";
import Ignore from 'ignore';
import App from "../App";
export default class FolderStructure {

    name = '';
    path = '';
    entry : any;
    files : FileStructure[];
    folders : FolderStructure[];

    constructor(folderPath : string) {
        let project_path = ConfigHelper.get('project_path');
        this.path = path.relative(project_path, folderPath);

        this.name = path.basename(this.path);

        this.files = this.getFiles(folderPath);
        this.folders = this.getStructure(folderPath);
    }

    public static getFiles(dir : string) {
        const entries = readdirSync(dir, {withFileTypes: true});
        let files :FileStructure[] = [];
        for (const entry of entries) {
            if (!entry.isDirectory() && !FolderStructure.isInIgnoreList(entry.name)) {
                files.push(new FileStructure(dir, entry))
            }
        }
        return files;
    }

    public static exists(dir : string) : boolean {
        return existsSync(dir);
    }
    public getFiles(dir: string): FileStructure[] {
        return FolderStructure.getFiles(dir);
    }

    private getStructure(path: string) : FolderStructure[] {
        let folders : FolderStructure[] = [];
        const entries = readdirSync(path, {withFileTypes: true});
        for (const entry of entries) {
            const res = resolve(path, entry.name);
            if (entry.isDirectory() && !FolderStructure.isInIgnoreList(res)) {
                folders.push(new FolderStructure(res));
            }
        }
        return folders;
    }



    public static isInIgnoreList(fileOrFolderPath : string): boolean {
        let relativePath = path.relative(App.Project.project_path, fileOrFolderPath)
        if (relativePath == '') return false;

        const ignore = Ignore();
        const excludedPatterns = ConfigHelper.get('exclude') as string[];
        ignore.add(excludedPatterns);
        return ignore.ignores(relativePath);
    }

    public static searchForStringInFiles(root: string, search: string): string {
        const files = fs.readdirSync(root);

        for (const file of files) {
            const fullPath = path.join(root, file);
            const stats = fs.statSync(fullPath);

            if (stats.isDirectory()) {
                return FolderStructure.searchForStringInFiles(fullPath, search);
            } else {
                if (path.extname(fullPath) === '.ts' || path.extname(fullPath) === '.js') {
                    const content = fs.readFileSync(fullPath, 'utf-8');
                    if (content.includes(search)) {
                        console.log(`${fullPath} contains the search string '${search}'`);
                        return content;
                    }
                }
            }
        }
        return '';
    }
}

const extract = require('extract-json-from-string');

export default class Helper {

    public static removeExtension(fileName : string) {
        if (fileName.lastIndexOf(".") == -1) return fileName;

        let name = fileName.substring(0, fileName.lastIndexOf("."));
        if (name == '') return fileName;
        return name;
    }

    static upperFirstLetter(name: string) {
        return name.charAt(0).toUpperCase() + name.slice(1);
    }

    static getJsons(content : string) : any[] {
        return extract(content)
    }
}

import ConfigHelper from "../utils/ConfigHelper";
import FolderStructure from "../utils/FolderStructure";

export default class ProjectStructure {
    public async getStructure() {
        let projectPath = ConfigHelper.get('project_path')!;
        let folderStructure = new FolderStructure(projectPath);

        return folderStructure;
    }

}

